import React from "react";
import Title from "../Title";

const Advantages = () => {
  const list = [
    {
      title: "Thoughtful rehabilitation plans",
      text: "The list of necessary rehabilitation measures is selected individually for each patient, based on their diagnosis and physical capabilities. The load gradually increases, which allows the patient to constantly develop, restoring health, mobility, speech, ability to work and self-care.",
      icon: <Icon1 />,
    },
    {
      title: "Conditions of stay in the center",
      text: "The patient's motivation is of the utmost importance in the rehabilitation process. If he lives in poor conditions and is preoccupied with everyday issues, rehabilitation cannot be complete. In our center, a person can fully concentrate on his recovery. Here, each patient is provided with the maximum level of everyday comfort.",
      icon: <Icon2 />,
    },
    {
      title: "An integrated approach to rehabilitation",
      text: "The Tulpar rehabilitation center uses various types of rehabilitation measures to ensure comprehensive recovery for the patient. It uses therapeutic exercise, physiotherapy, psychological training, and lifestyle modification. The methods are selected in such a way as to complement each other.",
      icon: <Icon3 />,
    },
    {
      title: "High-tech equipment",
      text: "The center is well equipped, which allows the most effective and high-tech procedures to be used for patient recovery. The most modern types of physiotherapy are used here.",
      icon: <Icon4 />,
    },
  ];
  return (
    <section className="section bg-main-dark-blue text-white">
      <div className="container space-y-12">
        <Title>Why Us?</Title>

        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-x-16 gap-y-12">
          {list.map((item, index) => (
            <Advantage key={index} {...item} />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Advantages;

type AdvantageProps = {
  title: string;
  text: string;
  icon: React.ReactNode;
};
const Advantage = ({ title, text, icon }: AdvantageProps) => {
  return (
    <div className="flex flex-col gap-4 text-center">
      <div className="w-full flex justify-center">{icon}</div>
      <h6 className="text-sm md:text-base font-semibold">{title}</h6>
      <p className="text-xs md:text-sm">{text}</p>
    </div>
  );
};

